import { Mapable } from "../../../../mixins/Mapable";
export default {
  mixins: [Mapable],
  name: "background-switcher",
  data: function data() {
    return {
      backgroundLayers: []
    };
  },
  methods: {
    /**
     * This function is executed, after the map is bound (see mixins/Mapable)
     */
    onMapBound: function onMapBound() {
      var _this = this;

      this.map.getLayers().getArray().forEach(function (layer) {
        if (layer.get("group") === "basemap") {
          _this.backgroundLayers.push(layer);
        }
      });
      var notActive = this.backgroundLayers.filter(function (layer) {
        return layer.getVisible() === false;
      });

      if (notActive.length === this.backgroundLayers.length) {
        notActive[0].setVisible(true);
      }
    },
    selectBaselayer: function selectBaselayer(item) {
      this.backgroundLayers.forEach(function (layer) {
        layer.setVisible(false);
      });
      item.setVisible(true);
    },
    getThumbnail: function getThumbnail(layer) {
      if (layer.thumbnail) {
        return layer.thumbnail;
      } else {
        return "img/background-layers/".concat(layer.get("name"), ".png");
      }
    }
  }
};
