import jrefs from "./utils/json-refs";
import colors from "./utils/colors";
import Property from "./components/Property.vue";
export default {
  name: "VJsonschemaForm",
  components: { Property },
  props: ["schema", "model", "options"],
  data() {
    return { modelWrapper: { root: this.model } };
  },
  computed: {
    resolvedSchema() {
      const resolved = jrefs.resolve(this.schema);

      return resolved;
    },
    fullOptions() {
      const httpLib = this.axios || this.$http || this.$axios;
      // we use the multi-themes functionality of vuetify2 as a clue of the version
      const vuetifyVersion = !this.$vuetify.theme.themes ? 1 : 2;
      // icons font is only variable in vuetify 2
      const iconfont =
        vuetifyVersion === 1
          ? "md"
          : (this.$vuetify.icons && this.$vuetify.icons.iconfont) || "mdi";
      const icons = {
        md: {
          calendar: "event",
          clock: "clock",
          info: "info",
          dropdown: "arrow_drop_down",
          dropup: "arrow_drop_up",
          add: "add",
          reorder: "reorder",
          delete: "delete"
        },
        mdi: {
          calendar: "mdi-calendar",
          clock: "mdi-clock",
          info: "mdi-information",
          dropdown: "mdi-menu-down",
          dropup: "mdi-menu-up",
          add: "mdi-plus",
          reorder: "mdi-reorder-horizontal",
          delete: "mdi-delete"
        }
      }[iconfont];

      const defaultOptions = {
        debug: false,
        httpLib,
        disableAll: false,
        colors,
        autoFoldObjects: false,
        allOfTabs: false,
        requiredMessage: this.$t("common.required"),
        noDataMessage: "No matching value found",
        searchMessage: "Search...",
        vuetifyVersion,
        tabsMode: "grow",
        locale: this.$i18n.locale
      };
      const fullOptions = Object.assign(
        {},
        defaultOptions,
        this.resolvedSchema["x-options"] || {},
        this.options || {}
      );
      fullOptions.icons = Object.assign(icons, fullOptions.icons || {});
      return fullOptions;
    }
  }
};
